onMounted(() => {
  // Get canvas and context
  const c = document.getElementById("header-canvas");
  const ctx = c.getContext("2d");
  // Keep track of time
  let t = 0;
  let speed = 1 / 10e6;
  let svgImg = null;

  function resizeCanvas() {
    c.width = document.body.offsetWidth;
    c.height = document.body.offsetHeight;
    drawGradient();
  }

  function drawSvg(input) {
    const header = document.getElementsByTagName('header')[0];
    const height = header.offsetHeight;
    const width = header.offsetWidth;
    let size = height > width ? width * 1.6 : height;
    let offset_x = height > width ? (width - size) / 1.4 : (width - size) / 2;
    // console.log(size);
    if (!svgImg) {
      let DOMURL = window.URL || window.webkitURL || window;
      svgImg = new Image();
      let svg = new Blob([input], {type: 'image/svg+xml'});
      let url = DOMURL.createObjectURL(svg);
      svgImg.src = url;
      svgImg.onload = function () {
        ctx.drawImage(svgImg, offset_x, 0, size, size);
        DOMURL.revokeObjectURL(url);
      }
    } else {
      ctx.drawImage(svgImg, offset_x, 0, size, size);
    }
  }

  window.addEventListener('resize', resizeCanvas);
  resizeCanvas();

  function loop() {
    drawGradient();
    t += speed;

    requestAnimationFrame(loop);
  }

  requestAnimationFrame(loop);

  function addNoise() {
    // Add noise
    let imageData = ctx.getImageData(0, 0, c.width, c.height);
    let data = imageData.data;

    for (let i = 0; i < data.length; i += 4) {
      let noise = Math.random() * 40 - 5 + t;

      data[i] = data[i] + noise;     // red
      data[i + 1] = data[i + 1] + noise; // green
      data[i + 2] = data[i + 2] + noise; // blue
    }

    ctx.putImageData(imageData, 0, 0);
  }

  function drawGradient() {
    // Clear background
    ctx.clearRect(0, 0, c.width, c.height);

    // Create gradient
    let grd = ctx.createLinearGradient(0, 0, c.width, c.height);
    grd.addColorStop(0, "#dbd897");
    grd.addColorStop(1, "#55a1d7");

    // Fill with gradient
    ctx.fillStyle = grd;
    ctx.fillRect(0, 0, c.width, c.height);

    drawSvg(svg)

    addNoise();
  }
})

